// Function to normalize user input
function normalizeInput(input) {
    const contractions = {
        "what's": "what is",
        "it's": "it is",
        "you're": "you are",
        "they're": "they are",
        "we're": "we are",
        "isn't": "is not",
        "aren't": "are not",
        "won't": "will not",
        "favourite": "favorite",
        "AI": "artificial intelligence",
        "how's": "how is",
        "who's": "who is",
        "colour": "color",
        "tell me about": "what is",
        "explain the": "explain",
        "organise": "organize",
        "explain": "what is",
        "goin": "going",
        "savoury":"savory",
        "soccer": "football",
        "cook":"make",
        "film":"movie",
        "do you play": "do you like",
        "kit kat":"kitkat",
        "thoughts on":"what is",
        "icecream":"ice cream",
      "i feel":"i'm feeling",

    };


    // Replace contractions
    let normalizedInput = input.toLowerCase();
    for (const [contraction, fullForm] of Object.entries(contractions)) {
        normalizedInput = normalizedInput.replace(new RegExp(contraction, 'g'), fullForm);
    }

    // Remove punctuation
    normalizedInput = normalizedInput.replace(/[^\w\s]/gi, '');

    return normalizedInput.trim();
}

// List of stop words to ignore
